mapper.select:
select count(*)
FROM BUILDING_TBL
WHERE B_CODE = #{B_CODE}

select count(*)
FROM BUILDING_TBL
WHERE B_NAME = #{B_NAME}

mapper.update:
update PARKING_TICKET_INFO_TBL
set p_type = #{p_type},
	hourly = #{hourly},
	price = #{price},
	reg_id = #{reg_id}
	WHERE p_code = #{p_code}
