mapper.select:
	
		select comp_seq, comp_section, comp_org, comp_branch, comp_master, comp_business, comp_category, comp_hp, comp_regidate, comp_status
        from (select rownum rNum, comp_seq, comp_section, comp_org, comp_branch, comp_master, comp_business, comp_category, comp_hp, comp_regidate, comp_status
              from (select comp_seq, comp_section, comp_org, comp_branch, comp_master, comp_business, comp_category, comp_hp, comp_regidate, comp_status from user_company_tbl order by comp_status asc, comp_seq desc))
        where rNum>=#{startNum}
        and rNum<=#{endNum}

	
		select b_code, b_area1, b_area2, b_address, b_name, b_floor, b_year, b_park, b_elev, b_heat, b_traffic, b_lat, b_lon, userid
        from (select rownum rNum, b_code, b_area1, b_area2, b_address, b_name, b_floor, b_year, b_park, b_elev, b_heat, b_traffic, b_lat, b_lon, userid
              from (select b_code, b_area1, b_area2, b_address, b_name, b_floor, b_year, b_park, b_elev, b_heat, b_traffic, b_lat, b_lon, userid from building_tbl order by userid asc, b_code desc))
        where rNum>=#{startNum}
        and rNum<=#{endNum}
